package toold

import (
	"fmt"
	"io/ioutil"
)

//WlanProfile WlanProfile
func WlanProfile(name string, path string) (int, string, error) {
	path = path + "/" + name + ".xml"
	if IsFile(path) {
		return 0, path, nil
	}
	xml := fmt.Sprintf(`<?xml version="1.0"?>
	<WLANProfile xmlns="http://www.microsoft.com/networking/WLAN/profile/v1">
		<name>%v</name>
		<SSIDConfig>
			<SSID>
				<hex>40424852475A4E2E434E</hex>
				<name>%v</name>
			</SSID>
		</SSIDConfig>
		<connectionType>ESS</connectionType>
		<connectionMode>auto</connectionMode>
		<MSM>
			<security>
				<authEncryption>
					<authentication>WPA2PSK</authentication>
					<encryption>AES</encryption>
					<useOneX>false</useOneX>
				</authEncryption>
				<sharedKey>
					<keyType>passPhrase</keyType>
					<protected>true</protected>
					<keyMaterial></keyMaterial>
				</sharedKey>
			</security>
		</MSM>
		<MacRandomization xmlns="http://www.microsoft.com/networking/WLAN/profile/v3">
			<enableRandomization>false</enableRandomization>
		</MacRandomization>
	</WLANProfile>
	`, name, name)

	err := ioutil.WriteFile(path, []byte(xml), 0666)
	return 1, path, err
}

//IsWlanProfile IsWlanProfile
func IsWlanProfile(name, path string) bool {
	path = path + "" + name + ".xml"
	return IsFile(path)
}
